import React, { useState } from "react";
import { Link } from "react-router-dom";

import logo from "./logo.svg";
import "./App.css";
import { useGrowl, Growl } from "./growl";

function App() {
	const [growlTime, setGrowlTime] = useState(3);
  const [active, setActive] = useGrowl();

  const handleChange = (e) => {
    setGrowlTime ({ growlTime: e.target.value });
    console.log(`${growlTime}`);
    console.log(`time is set to ${growlTime}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <Link className="App-link" to="/other">
          Go to the Other component
        </Link>
        <p>Set Growl display time in seconds:</p>
        <input type="number" id="growlTime" name="growlTime" value={growlTime}></input>
        <button onChange={handleChange}>Submit Display Time</button>
        <a className="App-link" href="#" onClick={() => void setActive(true)}>
          Click here to activate the growl
        </a>
      </header>
      <Growl
        displayTime={growlTime}
        onDismissed={() => setActive(false)}
        active={active}
        message="Hello World!"
      />
    </div>
  );
}

export default App;
